import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FiEdit2, FiTrash2, FiShare2, FiDownload, FiPlus } from "react-icons/fi";
import AddNewActivityModal from "./AddNewActivityModal";

export default function ItineraryPage() {
  const [days, setDays] = useState([
    { dayNumber: 1, activities: [] }
  ]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDay, setSelectedDay] = useState(null);
  const [editingDay, setEditingDay] = useState(null);
  const [editedTitle, setEditedTitle] = useState("");

  // Add Day
  const handleAddDay = () => {
    const newDayNumber = days.length + 1;
    setDays([...days, { dayNumber: newDayNumber, activities: [] }]);
  };

  // Delete Day
  const handleDeleteDay = (dayNumber) => {
    const updated = days
      .filter((d) => d.dayNumber !== dayNumber)
      .map((d, index) => ({ ...d, dayNumber: index + 1 }));
    setDays(updated);
  };

  // Save Day Title
  const handleSaveDayTitle = () => {
    const updated = days.map((d) =>
      d.dayNumber === editingDay ? { ...d, title: editedTitle } : d
    );
    setDays(updated);
    setEditingDay(null);
    setEditedTitle("");
  };

  // Add Activity
  const handleAddActivity = (activity) => {
    const updated = days.map((d) =>
      d.dayNumber === selectedDay
        ? { ...d, activities: [...d.activities, activity] }
        : d
    );
    setDays(updated);
    setIsModalOpen(false);
    setSelectedDay(null);
  };

  return (
    <div className="relative w-full min-h-screen bg-gray-100">

      {/* Background */}
      <img
        src="/home.jpg"
        alt="background"
        className="absolute inset-0 w-full h-full object-cover brightness-75"
      />
      <div className="absolute inset-0 bg-black/30"></div>

      <div className="relative z-10 max-w-6xl mx-auto py-10 px-4">

        {/* Navbar */}
        <nav className="fixed top-0 left-0 w-full z-50">
          <div className="max-w-15xl mx-auto px-6 py-4 flex items-center justify-between
              bg-white/10 backdrop-blur-lg rounded-b-3xl shadow-lg border border-white/20">
            <div className="flex items-center gap-4">
              <Link to="/" className="flex items-center gap-3">
                <img src="/logo.avif" alt="Logo" className="w-12 h-12 rounded-full border-2 border-white shadow-md"/>
                <h1 className="text-xl md:text-2xl font-bold text-white tracking-wide">
                  Travel Itinerary Planner
                </h1>
              </Link>
            </div>
            <div className="flex items-center gap-4 text-white">
              <Link to="/" className="px-4 py-2 rounded-xl border border-white/50 hover:bg-white hover:text-gray-900 transition-all shadow-md backdrop-blur-sm">
                Home
              </Link>
              <Link to="/register" className="px-4 py-2 rounded-xl border border-white/50 hover:bg-white hover:text-gray-900 transition-all shadow-md backdrop-blur-sm">
                Hello User
              </Link>
              <Link to="/" className="px-4 py-2 rounded-xl border border-red-300 hover:bg-red-500 hover:text-white hover:border-red-500 transition-all shadow-md backdrop-blur-sm">
                Logout
              </Link>
            </div>
          </div>
        </nav>

        {/* Trip Summary */}
        <div className="bg-white/90 backdrop-blur-md p-5 rounded-2xl shadow-lg flex justify-between items-center mb-8 mt-24">
          <div>
            <h2 className="text-xl md:text-2xl font-semibold">Nepal</h2>
            <p className="text-sm md:text-base text-gray-700">{days.length} days</p>
          </div>
          <div className="flex items-center gap-3">
            <button className="px-4 py-2 bg-white rounded-xl flex items-center gap-2 shadow-md hover:bg-gray-100 transition">
              <FiShare2 /> Share
            </button>
            <button className="px-4 py-2 bg-white rounded-xl flex items-center gap-2 shadow-md hover:bg-gray-100 transition">
              <FiDownload /> Export
            </button>
            <button onClick={() => { setSelectedDay(1); setIsModalOpen(true); }} className="px-4 py-2 bg-green-600 text-white rounded-xl flex items-center gap-2 shadow-md hover:bg-green-700 transition">
              <FiPlus /> Add Activity
            </button>
          </div>
        </div>

        {/* Days */}
        <div className="flex flex-col gap-6">
          {days.map((day) => (
            <div key={day.dayNumber} className="bg-white/90 backdrop-blur-xl rounded-3xl p-6 shadow-lg hover:shadow-2xl transition">

              {/* Header */}
              <div className="flex justify-between items-center mb-3">
                <h3 className="text-xl md:text-2xl font-semibold">
                  {editingDay === day.dayNumber ? (
                    <input
                      className="border px-2 py-1 rounded-md"
                      value={editedTitle}
                      onChange={(e) => setEditedTitle(e.target.value)}
                      autoFocus
                    />
                  ) : (
                    `Day ${day.dayNumber}`
                  )}
                </h3>

                <div className="flex gap-3 items-center">
                  {editingDay !== day.dayNumber ? (
                    <button onClick={() => { setEditingDay(day.dayNumber); setEditedTitle(day.title || `Day ${day.dayNumber}`); }} className="text-blue-600 hover:underline text-sm">Edit</button>
                  ) : (
                    <button onClick={handleSaveDayTitle} className="text-green-600 hover:underline text-sm">Save</button>
                  )}
                  <button onClick={() => handleDeleteDay(day.dayNumber)} className="text-red-600 hover:underline text-sm flex items-center gap-1">
                    <FiTrash2 /> Delete
                  </button>
                  <button onClick={handleAddDay} className="text-blue-600 hover:underline text-sm">+ Add day</button>
                </div>
              </div>

              {/* Activities */}
              <div className="space-y-4">
                {day.activities.length === 0 ? (
                  <p className="text-gray-500 text-sm">No activities yet.</p>
                ) : (
                  day.activities.map((act, i) => (
                    <div key={i} className="bg-white rounded-2xl p-4 shadow-md flex justify-between items-start hover:shadow-lg transition">
                      <div>
                        <p className="font-semibold text-gray-800">{act.title}</p>
                        <p className="text-sm text-gray-600">{act.description}<br/><span className="font-medium">{act.time}</span></p>
                        <p className="text-sm text-gray-700 mt-1">{act.location}</p>
                      </div>
                      <button className="text-gray-600 hover:text-black transition"><FiEdit2 size={18} /></button>
                    </div>
                  ))
                )}
              </div>

              {/* Add Activity Button */}
              <button onClick={() => { setSelectedDay(day.dayNumber); setIsModalOpen(true); }} className="w-full mt-6 py-2 bg-blue-600 text-white rounded-2xl border border-blue-600 hover:bg-blue-700 transition font-medium">
                + Add Activity
              </button>

            </div>
          ))}
        </div>

        {/* Modal */}
        <AddNewActivityModal isOpen={isModalOpen} onClose={() => { setIsModalOpen(false); setSelectedDay(null); }} onAddActivity={handleAddActivity} />

      </div>
    </div>
  );
}
