import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FiEdit2, FiShare2, FiDownload, FiPlus } from "react-icons/fi";
import AddNewActivityModal from "./AddNewActivityModal";

export default function ItineraryPage() {
  const [days, setDays] = useState([
    { dayNumber: 1, date: "Saturday, November 15, 2025", activities: [] }
  ]);
  const [selectedDay, setSelectedDay] = useState(1); // Day tab selected
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Add new day
  const handleAddDay = () => {
    const newDayNumber = days.length + 1;
    setDays([
      ...days,
      { dayNumber: newDayNumber, date: `Day ${newDayNumber} date not set`, activities: [] }
    ]);
    setSelectedDay(newDayNumber);
  };

  // Delete day
  const handleDeleteDay = (dayNumber) => {
    const updated = days
      .filter(d => d.dayNumber !== dayNumber)
      .map((d, index) => ({ ...d, dayNumber: index + 1 }));
    setDays(updated);
    setSelectedDay(updated.length ? updated[0].dayNumber : null);
  };

  // Add activity to selected day
  const handleAddActivity = (activity) => {
    const updated = days.map(d =>
      d.dayNumber === selectedDay
        ? { ...d, activities: [...d.activities, activity] }
        : d
    );
    setDays(updated);
    setIsModalOpen(false);
  };

  const currentDay = days.find(d => d.dayNumber === selectedDay);

  return (
    <div className="relative w-full min-h-screen bg-gray-100 p-6">

      {/* Days Tabs */}
      <div className="flex gap-2 mb-6">
        {days.map(day => (
          <button
            key={day.dayNumber}
            onClick={() => setSelectedDay(day.dayNumber)}
            className={`flex items-center gap-2 px-4 py-2 rounded-full border ${
              day.dayNumber === selectedDay
                ? "bg-blue-600 text-white border-blue-600"
                : "bg-white text-gray-800 border-gray-300"
            }`}
          >
            <span>ðŸ“…</span> Day {day.dayNumber}
          </button>
        ))}
        <button
          onClick={handleAddDay}
          className="flex items-center gap-2 px-4 py-2 rounded-full border border-gray-300 bg-white text-gray-800 hover:bg-gray-100"
        >
          + Add Day
        </button>
      </div>

      {/* Selected Day Content */}
      {currentDay && (
        <div className="bg-white p-6 rounded-2xl shadow-md">
          <h2 className="text-xl font-semibold mb-4">Day {currentDay.dayNumber}</h2>
          <p className="text-gray-600 mb-4">{currentDay.date}</p>

          <div className="space-y-4 mb-4">
            {currentDay.activities.length === 0 ? (
              <p className="text-gray-500">No activities added yet.</p>
            ) : (
              currentDay.activities.map((act, i) => (
                <div
                  key={i}
                  className="p-4 rounded-xl bg-gray-100 flex justify-between items-start"
                >
                  <div>
                    <p className="font-semibold">{act.title}</p>
                    <p className="text-sm text-gray-600">{act.description}</p>
                    <p className="text-sm text-gray-500">{act.time}</p>
                  </div>
                  <button className="text-gray-600 hover:text-black">
                    <FiEdit2 />
                  </button>
                </div>
              ))
            )}
          </div>

          <button
            onClick={() => setIsModalOpen(true)}
            className="px-6 py-2 bg-blue-600 text-white rounded-full hover:bg-blue-700"
          >
            + Add Activity
          </button>
        </div>
      )}

      {/* Modal */}
      <AddNewActivityModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onAddActivity={handleAddActivity}
      />
    </div>
  );
}
