import React, { useRef } from 'react';
import { LoadScript, StandaloneSearchBox } from '@react-google-maps/api';

const libraries = ['places'];

export default function LocationAutocomplete({ onPlaceSelected, defaultValue = '' }) {
  const boxRef = useRef(null);

  const handlePlacesChanged = () => {
    const places = boxRef.current?.getPlaces();
    if (!places || places.length === 0) return;
    const p = places[0];
    const location = {
      name: p.name || p.formatted_address || '',
      address: p.formatted_address || '',
      lat: p.geometry?.location?.lat?.() || null,
      lng: p.geometry?.location?.lng?.() || null,
    };
    onPlaceSelected(location);
  };

  return (
    <LoadScript googleMapsApiKey={import.meta.env.VITE_GOOGLE_MAPS_API_KEY} libraries={libraries}>
      <StandaloneSearchBox
        onLoad={(ref) => (boxRef.current = ref)}
        onPlacesChanged={handlePlacesChanged}
      >
        <input
          type="text"
          defaultValue={defaultValue}
          placeholder="Search location..."
          className="w-full px-3 py-2 bg-gray-100 border border-gray-300 rounded-lg focus:outline-none"
        />
      </StandaloneSearchBox>
    </LoadScript>
  );
}
