import React from "react";
import { useNavigate, Link } from "react-router-dom";

export default function HomePage() {
  const navigate = useNavigate();

  const handleStart = () => {
    navigate("/login");
  };

  return (
    <div className="w-full h-screen relative">
      {/* Background Image */}
      <img
        src="/home.jpg" 
        alt="Travel Background"
        className="absolute inset-0 w-full h-full object-cover"
      />

      {/* Overlay */}
      <div className="absolute inset-0 bg-black/40"></div>

      {/* Navbar */}
      <nav className="absolute top-0 z-20 w-full flex items-center justify-between px-8 py-5 text-white font-medium">
            <div className="flex items-center gap-3">
              <img src="/logo.avif" alt="Logo" className="w-12 h-12 rounded-full" />
              <h1 className="text-xl font-bold">Travel Itinerary Planner</h1>
            </div>

            <div className="flex items-center gap-4">
              <Link 
                to="/" 
                className="px-4 py-2 rounded-full border border-white/60 hover:bg-white hover:text-gray-900 transition font-medium"
              >
                Home
              </Link>

              <Link 
                to="/register" 
                className="px-4 py-2 rounded-full border border-white/60 hover:bg-white hover:text-gray-900 transition font-medium"
              >
                Register
              </Link>
            </div>
          </nav>

      {/* Hero Text */}
      <div className="relative z-10 w-full h-full flex flex-col justify-center items-center text-white text-center px-4">
        <h2 className="text-3xl md:text-5xl font-semibold mb-4">
          Ready for your next Adventure?
        </h2>
        <p className="text-lg md:text-2xl mb-6">
          Plan your trip Â· Travel like a pro
        </p>

        {/* Start Button */}
        <button 
          onClick={handleStart}
          className="px-8 py-10 bg-white text-gray-800 rounded-full shadow-md text-lg font-medium hover:bg-gray-200 transition"
        >
          Start
        </button>
      </div>
    </div>
  );
}
