import React from "react";
import { Link } from "react-router-dom";
import { FiEdit2, FiShare2, FiDownload, FiPlus } from "react-icons/fi";

export default function ItineraryPage() {
  return (
    <div className="relative w-full min-h-screen">

      {/* Background */}
      <img
        src="/home.jpg"
        alt="background"
        className="absolute inset-0 w-full h-full object-cover"
      />
      <div className="absolute inset-0 bg-black/25"></div>

      {/* Content Wrapper */}
      <div className="relative z-10 max-w-6xl mx-auto py-6 px-4">

        {/* Navbar */}
        <nav className="flex items-center mb-6 text-white w-full">
          <div className="flex items-center gap-3 flex-1">
            <img src="/logo.avif" alt="Logo" className="w-12 h-12 rounded-full" />
            <h1 className="text-xl font-bold">Travel Itinerary Planner</h1>
          </div>

          <div className="flex items-center gap-8 text-lg justify-end">
            <Link to="/" className="hover:text-gray-300">Home</Link>
            <Link to="/profile" className="hover:text-gray-300">Hello user</Link>
            <Link to="/" className="hover:text-gray-300">Logout</Link>
          </div>
        </nav>

        {/* Trip Summary Bar */}
        <div className="bg-white/80 backdrop-blur-md p-4 rounded-xl shadow flex justify-between items-center mb-6">
          <div>
            <h2 className="text-lg font-semibold">Nepal</h2>
            <p className="text-sm text-gray-700">2 days, 1 activities</p>
          </div>

          <div className="flex items-center gap-3">
            <button className="px-3 py-2 bg-white rounded-lg flex items-center gap-2 shadow hover:bg-gray-100">
              <FiShare2 /> Share
            </button>
            <button className="px-3 py-2 bg-white rounded-lg flex items-center gap-2 shadow hover:bg-gray-100">
              <FiDownload /> Export
            </button>
            <button className="px-3 py-2 bg-green-600 text-white rounded-lg flex items-center gap-2 shadow hover:bg-green-700">
              <FiPlus /> Add Activity
            </button>
          </div>
        </div>

        {/* Main Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">

          {/* Left Content */}
          <div className="lg:col-span-2 flex flex-col gap-6">
            <div className="bg-white/80 rounded-2xl p-6 shadow">
              <div className="flex justify-between mb-2">
                <h3 className="text-xl font-semibold">Day 1, Day 2</h3>
                <button className="text-sm text-blue-600 hover:underline">+ Add day</button>
              </div>

              <p className="text-gray-700 font-medium mb-4">Saturday, November 15, 2025</p>

              <div className="space-y-4">
                <div className="bg-white rounded-xl p-4 shadow flex justify-between items-start">
                  <div>
                    <p className="font-semibold">Check-in at Hotel</p>
                    <p className="text-sm text-gray-600">Arrival and hotel check-in <br /><span className="font-medium">10:00</span></p>
                    <p className="text-sm text-gray-700 mt-1">Downtown Hotel</p>
                  </div>

                  <button className="text-gray-600 hover:text-black"><FiEdit2 /></button>
                </div>
              </div>

              <button className="w-full mt-4 py-2 bg-white rounded-xl border border-gray-300 hover:bg-gray-100">+ Add Activity</button>
            </div>
          </div>

          {/* Right Content */}
          <div className="bg-white/80 rounded-2xl p-4 shadow">
            <h3 className="font-semibold text-xl mb-3">Map view</h3>
            <div className="rounded-xl overflow-hidden mb-4">
              <img src="/map.jpg" alt="Map" className="w-full h-48 object-cover" />
            </div>
            <h4 className="font-medium">Location</h4>
            <p className="text-sm text-gray-700">Location 1</p>
            <p className="text-sm text-gray-700">Location 2</p>
          </div>

        </div>
      </div>
    </div>
  );
}
import { useState, useMemo } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import { ArrowLeft, Plus, Share2, Download, Calendar } from 'lucide-react';
import { Button } from './ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import { DayPlanner } from './DayPlanner';
import { DestinationSearch } from './DestinationSearch';
import { MapView } from './MapView';
import { toast } from 'sonner@2.0.3';
import { Card } from './ui/card';

export function ItineraryPlanner({ itineraryName, onBack }) {
  const [days, setDays] = useState([
    {
      id: '1',
      date: '2025-11-15',
      activities: [
        {
          id: 'a1',
          title: 'Check-in at Hotel',
          description: 'Arrival and hotel check-in',
          time: '14:00',
          location: 'Downtown Hotel',
          category: 'accommodation',
          lat: 40.7128,
          lng: -74.0060
        },
        {
          id: 'a2',
          title: 'City Walking Tour',
          description: 'Explore the historic downtown area',
          time: '16:00',
          location: 'City Center',
          category: 'activity',
          lat: 40.7589,
          lng: -73.9851
        },
        {
          id: 'a3',
          title: 'Dinner at Local Restaurant',
          description: 'Try authentic local cuisine',
          time: '19:00',
          location: 'Restaurant District',
          category: 'food',
          lat: 40.7489,
          lng: -73.9680
        }
      ]
    },
    {
      id: '2',
      date: '2025-11-16',
      activities: [
        {
          id: 'a4',
          title: 'Museum Visit',
          description: 'Visit the art museum',
          time: '10:00',
          location: 'Art Museum',
          category: 'activity',
          lat: 40.7794,
          lng: -73.9632
        },
        {
          id: 'a5',
          title: 'Lunch at Café',
          description: 'Light lunch break',
          time: '13:00',
          location: 'Museum Café',
          category: 'food',
          lat: 40.7794,
          lng: -73.9632
        }
      ]
    }
  ]);

  const [activeDay, setActiveDay] = useState('1');
  const [showSearch, setShowSearch] = useState(false);

  const allActivities = useMemo(
    () => days.flatMap(day => day.activities),
    [days]
  );

  const handleAddDay = () => {
    const lastDayDate = new Date(days[days.length - 1].date);
    const newDate = new Date(lastDayDate.getTime() + 86400000)
      .toISOString()
      .split('T')[0];

    const newDay = {
      id: String(days.length + 1),
      date: newDate,
      activities: []
    };

    setDays([...days, newDay]);
    setActiveDay(newDay.id);
    toast.success('New day added to itinerary');
  };

  const handleReorderActivities = (dayId, newActivities) => {
    setDays(days.map(day =>
      day.id === dayId ? { ...day, activities: newActivities } : day
    ));
  };

  const handleAddActivity = (dayId, activity) => {
    setDays(days.map(day =>
      day.id === dayId ? { ...day, activities: [...day.activities, activity] } : day
    ));
    toast.success('Activity added');
  };

  const handleDeleteActivity = (dayId, activityId) => {
    setDays(days.map(day =>
      day.id === dayId
        ? { ...day, activities: day.activities.filter(a => a.id !== activityId) }
        : day
    ));
    toast.success('Activity removed');
  };

  const handleEditActivity = (dayId, activityId, updatedActivity) => {
    setDays(days.map(day =>
      day.id === dayId
        ? { ...day, activities: day.activities.map(a => a.id === activityId ? updatedActivity : a) }
        : day
    ));
    toast.success('Activity updated');
  };

  const handleShare = () => {
    toast.success('Itinerary link copied to clipboard!');
  };

  const handleExport = () => {
    toast.success('Itinerary exported as PDF');
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="min-h-screen bg-gray-50">
        {/* Header */}
        <div className="bg-white border-b sticky top-0 z-10">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <Button variant="ghost" size="icon" onClick={onBack}>
                  <ArrowLeft className="w-5 h-5" />
                </Button>
                <div>
                  <h1 className="text-xl">{itineraryName}</h1>
                  <p className="text-sm text-gray-500">
                    {days.length} {days.length === 1 ? 'day' : 'days'} • {allActivities.length} activities
                  </p>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <Button variant="outline" size="sm" onClick={handleShare}>
                  <Share2 className="w-4 h-4 mr-2" />
                  Share
                </Button>
                <Button variant="outline" size="sm" onClick={handleExport}>
                  <Download className="w-4 h-4 mr-2" />
                  Export
                </Button>
                <Button size="sm" onClick={() => setShowSearch(!showSearch)}>
                  <Plus className="w-4 h-4 mr-2" />
                  Add Activity
                </Button>
              </div>
            </div>
          </div>
        </div>

        {/* Search Panel */}
        {showSearch && (
          <div className="bg-blue-50 border-b">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
              <DestinationSearch
                onSelectDestination={(destination) => {
                  const newActivity = {
                    id: `a${Date.now()}`,
                    title: destination.name,
                    description: destination.description,
                    time: '10:00',
                    location: destination.name,
                    category: 'activity',
                    lat: destination.lat,
                    lng: destination.lng
                  };
                  handleAddActivity(activeDay, newActivity);
                  setShowSearch(false);
                }}
                onClose={() => setShowSearch(false)}
              />
            </div>
          </div>
        )}

        {/* Main Content */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Left Side - Day Planner */}
            <div className="lg:col-span-2">
              <Card className="p-6">
                <Tabs value={activeDay} onValueChange={setActiveDay}>
                  <div className="flex items-center justify-between mb-4">
                    <TabsList>
                      {days.map((day, index) => (
                        <TabsTrigger key={day.id} value={day.id}>
                          <Calendar className="w-4 h-4 mr-2" />
                          Day {index + 1}
                        </TabsTrigger>
                      ))}
                    </TabsList>
                    <Button variant="outline" size="sm" onClick={handleAddDay}>
                      <Plus className="w-4 h-4 mr-2" />
                      Add Day
                    </Button>
                  </div>
                  {days.map((day) => (
                    <TabsContent key={day.id} value={day.id} className="mt-0">
                      <DayPlanner
                        day={day}
                        onReorderActivities={(newActivities) => handleReorderActivities(day.id, newActivities)}
                        onDeleteActivity={(activityId) => handleDeleteActivity(day.id, activityId)}
                        onEditActivity={(activityId, updatedActivity) => handleEditActivity(day.id, activityId, updatedActivity)}
                        onAddActivity={(activity) => handleAddActivity(day.id, activity)}
                      />
                    </TabsContent>
                  ))}
                </Tabs>
              </Card>
            </div>

            {/* Right Side - Map */}
            <div className="lg:col-span-1">
              <div className="sticky top-24">
                <MapView activities={days.find(d => d.id === activeDay)?.activities || []} />
              </div>
            </div>
          </div>
        </div>
      </div>
    </DndProvider>
  );
}
